package GoogleChallenge3Code2A2;

import java.math.BigInteger;
import java.util.HashMap;

/**
 * Created by ShIvS on 6/17/2017.
 */
public class Generarate2sGP {
    public static void main(String[] args) {
        BigInteger twosMultiples = generate2toThePower(1031);
        System.out.println(twosMultiples.toString());
    }
    private static BigInteger generate2toThePower(int power) {
        BigInteger twosMultiples = new BigInteger("1");
        BigInteger two = new BigInteger("2");
        for (int i = 0; i < power; i++) {
            twosMultiples = twosMultiples.multiply(two);
        }
        return twosMultiples;
    }
}
